#include "int2048_t.hpp"

int evaluate(const int2048_t &num) {
#define LEVEL 11

    int offest[] = {
        1,
        2,
        4,
        8,
        16,
        32,
        64,
        128,
        256,
        512,
        1024
    };
    char maskbuf[][3000] = {
        "",
        "",
        "",
        "",
        "",
        "",
        "",
        "",
        "",
        "",
        ""
    };

    int2048_t mask[LEVEL];
    for (int i = 0; i < LEVEL; i++)
        mask[i] = int2048_t(maskbuf[i]);

    int2048_t n = num;
    for (int i = 0; i < LEVEL; i++)
        n = (n & mask[i]) + ((n >> offest[i]) & mask[i]);

    int answer = 0;
    int t = 1;
    int2048_t tester[] = {
        int2048_t("1"),
        int2048_t("10"),
        int2048_t("100"),
        int2048_t("1000"),
        int2048_t("10000"),
        int2048_t("100000"),
        int2048_t("1000000"),
        int2048_t("10000000"),
        int2048_t("100000000"),
        int2048_t("1000000000"),
        int2048_t("10000000000")
    };
    int pos = 0;
    for (int i = 0; i < LEVEL; i++) {
        if (n & tester[pos++])
            answer += t;
        t <<= 1;
    }

    return answer;

#undef LEVEL
}
