#include "int2048_t.hpp"

int evaluate(const int2048_t &num) {
#define LEVEL 6

    int offest[] = {
        1,
        4,
        16,
        64,
        256
    };
    char maskbuf[][3000] = {
        "",
        "",
        "",
        "",
        "",
        ""
    };

    int2048_t mask[LEVEL];
    for (int i = 0; i < LEVEL; i++)
        mask[i] = int2048_t(maskbuf[i]);

    int2048_t n = num;
    for (int i = 0; i < 5; i++)
        n = (n & mask[i])
            + ((n >> offest[i]) & mask[i])
            + ((n >> (offest[i] * 2)) & mask[i])
            + ((n >> (offest[i] * 3)) & mask[i]);
    n = (n & mask[5]) + ((n >> 1024) & mask[5]);

    int answer = 0;
    int t = 1;
    int2048_t tester("1");
    for (int i = 0; i <= 10; i++) {
        if (n & tester)
            answer += t;

        t <<= 1;
        if (i < 10)
            tester = tester << 1;
    }

    return answer;

#undef LEVEL
}
